INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1773, 'Full Stack Web Developer at Discogs (Beaverton, OR) (allows remote)', '&lt;p&gt;We are looking for a Full Stack Developer to join our growing Portland, OR&amp;nbsp;office. Consideration will also be given to exceptional remote candidates. &amp;nbsp;The ideal candidate will be comfortable working with from everything from backend datastores to application servers, JavaScript, and HTML/CSS, but primarily with Python and MySQL. As a member of a small team focused on specific software components, you will help develop and improve software services that power Discogs. This role will play a big part in supporting and growing our community of collectors, buyers, and sellers. Considering this will require working closely with other Discogs software teams, it is critical to exercise a strong sense of initiative and collaboration.&lt;/p&gt;', 'Beaverton, OR', 'http://careers.stackoverflow.com/jobs/97150/full-stack-web-developer-discogs', 'Sun, 04 Oct 2015 21:02:02 Z', '2015-10-04T21:02:02Z', 1773);
