INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1775, '.NET Engineer at The Nerdery (Chicago, IL)', '&lt;p&gt;The Nerdery is seeking a .NET Software Engineer to work on a wide variety of projects as part of our team.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;About the Software Engineering Team&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;At the Nerdery, our Software Engineering team believes strongly in quality workmanship, meaningful collaboration, and outstanding customer service. We pride ourselves on being able to partner with our clients to develop custom software to solve their business problems.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;From startups to corporations, our team makes an impact on millions of users every day.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;About the Position&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As a .NET Software Engineer at The Nerdery, you''ll have the opportunity to work on projects for a wide range of clients. We have opportunities to solve customer projects with a wide variety of frameworks and platforms such as ASP.NET MVC, Web Api, Umbraco, Sitecore, Entity Framework and many more. The position provides opportunities to expand your horizons to reach your personal development goals.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Most of our engineers contribute to a multitude of projects each year. You&amp;rsquo;ll never feel like you&amp;rsquo;re stuck working on a single project for the rest of your career.&lt;/p&gt;', 'Chicago, IL', 'http://careers.stackoverflow.com/jobs/94732/net-engineer-the-nerdery', 'Sun, 04 Oct 2015 21:02:02 Z', '2015-10-04T21:02:02Z', 1775);
