INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1780, 'Web Applications Engineer (Rails+) at Sharethrough (Austin, TX)', '&lt;p&gt;As a full-stack engineer here you&amp;rsquo;ll contribute to everything from our 3rd-Party CoffeeScript deployed across hundreds of publishers like Time Inc. and Forbes, to our self-serve native content exchange with significant growth opportunities - from jumping into our Scala-based data pipeline and microservices to provisioning AWS resources with Chef.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Let&amp;rsquo;s talk if&amp;hellip;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;You speak (or are interested in speaking!) our stack: Rails, RSpec, CoffeeScript, Backbone.js, HAML, LESS, etc.&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;You take pride in treating software development as a craft and delivering SOLID well-tested code.&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;You derive deep satisfaction from participating in the problem definition and solution process with a diverse group from around the company.&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Austin, TX', 'http://careers.stackoverflow.com/jobs/55841/web-applications-engineer-rails-plus-sharethrough', 'Sun, 04 Oct 2015 21:02:02 Z', '2015-10-04T21:02:02Z', 1780);
