INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1783, 'Data Analytics Engineer at Sharethrough (San Francisco, CA)', '&lt;p&gt;&lt;span&gt;Sharethrough is looking for engineers interested in building data products and infrastructure to power our business and provide insight to our customers. &amp;nbsp;You''ll be taking the lead on the design and implementation of a data warehousing infrastructure used by our product, engineering and operational teams.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;What You''ll Be Doing:&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Greenfield architecture and construction of a modern data warehouse&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Working with new technologies such as Apache Spark and Amazon Redshift&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Deriving insight from large quantities of data, starting with our 500GB/day performance pipeline&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Collaborating with data science and business intelligence teams on visualization and data wrangling platforms such as Looker&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;Let&amp;rsquo;s talk if...&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;You&amp;rsquo;ve got experience with SQL, ETL, python/shell scripting, and MPP Data Warehousing&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;You&amp;rsquo;ve worked on or are interested in data integrity governance and semantic layer design&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;You possess an intense curiosity about data and a strong commitment to practical problem solving&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'San Francisco, CA', 'http://careers.stackoverflow.com/jobs/55843/data-analytics-engineer-sharethrough', 'Sun, 04 Oct 2015 21:02:02 Z', '2015-10-04T21:02:02Z', 1780);
