INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1789, 'Mobile App Developer, Support at Xamarin (Boston, MA) (allows remote)', '&lt;p&gt;&lt;strong&gt;Your Role:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As a Mobile App Developer on the &lt;a href="http://xamarin.com/support" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;Customer Support&lt;/a&gt; team, you&amp;rsquo;ll be the first point of contact with Xamarin&amp;rsquo;s customers. You&amp;rsquo;ll work closely with product engineers and guide customers from a potential problem to a successful solution, delighting customers in the process.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Key Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Work through support cases and prioritize cases appropriately&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Write sample applications to investigate potential bugs and issues within Xamarin Studio/Visual Studio with Xamarin.iOS and Xamarin.Android&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work with customers to help them achieve their goals using Xamarin technologies&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Monitor issues on the Xamarin Mailing Lists, Forums, as well as other public channels such as StackOverflow&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What We''re Looking For:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Sample artifacts of your coding acumen, examples include: GitHub projects, or other open source participation, sample bug reports filed, Stack Overflow reputation, technical Blog&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Xamarin.iOS and Xamarin.Android experience preferred but not required&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;A strong interest in building mobile apps on iOS or Android and proficiency with C# and .NET (or a demonstrable willingness and ability to learn quickly)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Excellent written communication skills&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Ability to adapt to different scenarios because everyday is different!&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Boston, MA', 'http://careers.stackoverflow.com/jobs/97136/mobile-app-developer-support-xamarin', 'Sun, 04 Oct 2015 21:02:02 Z', '2015-10-04T21:02:02Z', 1789);
