INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1791, 'Senior QA Automation Engineer - Big Data at FourV Systems (Baltimore, MD)', '&lt;p&gt;Are you a quality assurance engineer with big, fast data experience looking to make an impact with a rapidly growing team of talented developers working on delivering a transformative product? Are you highly motivated to make a real impact in the trajectory of our company?&amp;nbsp;FourV Systems is looking for a&amp;nbsp;senior QA&amp;nbsp;engineer like you for a transformative big data analytics platform.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;In the Senior QA Engineer &amp;ndash; Big Data role, you will be responsible for participating in the design and development efforts of our big data testing infrastructure for functional and performance testing. The testing infrastructure will exercise patented machine learning algorithms for analysis of large amounts of cross-domain data to meet customer needs.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This is a great opportunity to join early and influence the technology selection, design, and have ownership of the infrastructure.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Key Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Develop and execute maintainable automation tests for acceptance, functional, and regression test cases against cutting edge distributed systems and big data analytics&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Investigate and debug test failures, updating tests or reporting bugs as necessary and provide test coverage analysis based on automation results&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work closely with software engineers on Agile teams to deliver quality features&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Participate in architecture discussions to address long-term design objectives&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Baltimore, MD', 'http://careers.stackoverflow.com/jobs/97127/senior-qa-automation-engineer-big-data-fourv-systems', 'Sun, 04 Oct 2015 20:02:02 Z', '2015-10-04T20:02:02Z', 1791);
