INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1801, 'Senior/Lead UI Engineer - Big Data Analytics at FourV Systems (Baltimore, MD)', '&lt;p&gt;Are you a user interface&amp;nbsp;engineer? with big, fast data experience? Are you looking to make an impact with a rapidly growing team of talented developers working on delivering a transformative product?&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Are you highly motivated to make a real impact in the trajectory of our company? FourV Systems is looking for full-stack UI engineers for a transformative big data analytics platform.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;In the Big Data Analytics &amp;ndash; Lead UI Engineer role, you will be responsible for participating in the design and development efforts of our big data user interfaces for the configuration and presentation of ETL interfaces, reporting, and visualization efforts.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This is a great opportunity to join early and influence the technology selection, design, and have ownership of the infrastructure.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Key Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Generate software designs and determine levels of effort for new product features in cutting edge big data analytics&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop software written in Javascript, including backbone.js, d3.js, and SVG rendering and other languages as needed&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work closely with QA and other software engineers on Agile teams to deliver quality features&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Participate in architecture discussions to address long-term design objectives&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Baltimore, MD', 'http://careers.stackoverflow.com/jobs/97121/senior-lead-ui-engineer-big-data-analytics-fourv-systems', 'Sun, 04 Oct 2015 19:02:02 Z', '2015-10-04T19:02:02Z', 1791);
