INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1813, 'Logistics Software Engineer at Haugen Software (Houston, TX)', '&lt;p&gt;Haugen Software develops a suite of tools that serve the freight and commodities trading industries. Every day, we solve complex problems for clients who are responsible for the flow of commodities shipments around the world.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;In a typical week, you might&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Spend 80% of your time developing new functionality. This is an engineering role first, so your primary responsibility is building, testing, and deploying great code.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work with end users to source and validate design ideas&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Participate in product roadmap planning&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Contribute to technical strategy and architecture decisions. How do our code bases need to evolve to accommodate future development? We recognize that refactoring and maintenance are critical to long-term project health.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Our stack&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Single-page app written in ClojureScript/reagent (plus a few older pieces in Knockout.js)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Backend web service written in idiomatic, scalaz-heavy Scala&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Hybrid event-sourced persistence model&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Hosted in Docker containers on AWS, managed with Ansible&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Houston, TX', 'http://careers.stackoverflow.com/jobs/96160/logistics-software-engineer-haugen-software', 'Sun, 04 Oct 2015 18:02:02 Z', '2015-10-04T18:02:02Z', 1813);
