INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1826, 'Lead Full Stack Developer at Vanare (New York, NY)', '&lt;p&gt;Vanare is a young and quickly growing online wealth management platform looking to hire a lead full stack developer. We are looking for a technically skilled individual to join our team full time. The developer would work closely with the CTO and an offshore development team to build financial software for investment advisors.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Responsibilities would cover the entire software lifecycle including hands-on development, code reviews, testing, deployment, and documentation. Vanare operates in an Agile work environment, meaning the developer should be comfortable transitioning between tasks and quickly solving technical problems as they arise. The Vanare office is located in New York City.&lt;/p&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/93914/lead-full-stack-developer-vanare', 'Sun, 04 Oct 2015 18:02:02 Z', '2015-10-04T18:02:02Z', 1826);
