INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1833, 'Mobile Application Developer at BrainCheck (Houston, TX)', '&lt;p&gt;BrainCheck is a funded Digital Health startup developing an innovative platform for tracking cognitive fitness and assessing conditions such as concussion and memory impairment. We sell to schools and athletic teams, industries where it is critical to be alert (e.g. oil &amp;amp; gas, trucking), as well as directly to individuals (especially athletes, parents, and those concerned about aging family members). Our state-of-the art technology is mobile-first (iPad &amp;amp; Android), gamified to appeal to a wide variety of test-takers, and HIPAA compliant.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;If you have a passion for neuroscience, big data, big ideas, and building technologies that can make a real difference in people&amp;rsquo;s lives, this position is for you.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;You''ll:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Add new features to the existing app/backend in an elegant and maintainable manner&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Think through technical challenges and build clean solutions&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Maintain the existing application and consult with the team about possible technical issues down the road&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Deploy on a regular basis (daily/weekly) and iterate quickly&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Help maintain foundational open source projects&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Houston, TX', 'http://careers.stackoverflow.com/jobs/97095/mobile-application-developer-braincheck', 'Sun, 04 Oct 2015 18:02:02 Z', '2015-10-04T18:02:02Z', 1833);
