INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1841, 'Lead Software Engineer, DevOps - AWS/EC2 at Cimpress (Vistaprint''s Parent Company) (Lexington, MA)', '&lt;p&gt;As a Lead Developer you will work with the Infrastructure, Operations, and Release Engineering team. Responsibilities will include management of the group&amp;rsquo;s AWS infrastructure including automation of various processes such as provisioning, deployment, scaling, log analysis, unit testing, integration testing, etc. In addition, you&amp;rsquo;ll also problem solve production issues (site/service performance, connectivity/reachability) related to infrastructure and collaborate with site/service developers to achieve overall operational goals.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Extend existing provisioning and deployment automation&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Resolve production issues related to infrastructure&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Determine ways of leveraging AWS services to replace in-house or other 3&lt;sup&gt;rd&lt;/sup&gt; party components (if appropriate)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Collaborate with site and service developers to meet overall operational goals&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Qualifications&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Proficiency installing and administering Windows and Linux server Operating systems&lt;/li&gt;&lt;br /&gt;&lt;li&gt;A working understanding of platform-specific scripting languages (PowerShell, Shell, Ruby)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience with automation/configuration management using either Puppet, Chef or an equivalent&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Ability to use a wide variety of open source technologies and cloud services (experience with AWS is required)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Familiarity working in an agile software development environment supporting continuous delivery&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Knowledge of best practices and IT operations in an always-up, always-available service&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience developing tools and utilities in an object-oriented language (C# or Java preferred)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience with leveraging AWS APIs in process automation is a plus&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Lexington, MA', 'http://careers.stackoverflow.com/jobs/89925/lead-software-engineer-devops-aws-ec2-cimpress-vistaprints-parent', 'Sun, 04 Oct 2015 17:02:02 Z', '2015-10-04T17:02:02Z', 1837);
