INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1844, 'JavaScript Engineer at Football Radar (London, UK)', '&lt;p&gt;Football Radar is a young, profitable company which does advanced statistical analysis of football. We are looking for an experienced JavaScript Engineer to help us deliver highly effective, maintainable user interfaces. You will have the opportunity to be involved in all areas of the platform, from trading applications to internal monitoring tools.&lt;/p&gt;', 'London, UK', 'http://careers.stackoverflow.com/jobs/82255/javascript-engineer-football-radar', 'Sun, 04 Oct 2015 17:02:02 Z', '2015-10-04T17:02:02Z', 1842);
