INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1847, 'Data Engineer at Earnest Research (New York, NY)', '&lt;p&gt;Passion for data: Data is what drives this group. Our Data Science team works very closely with various data sources. The ideal candidate should have a passion to mine data and derive insights. This position requires a highly motivated problem solver with strong analytical ability and attention to detail to enable the data analysts and data scientists.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; Analyzing data to identify gaps and inconsistencies in data&lt;br&gt;&amp;bull; Perform extraction and transformation of unstructured data from various data sources&lt;br&gt;&amp;bull; Improving our platform with additional innovations and algorithms&lt;br&gt;&amp;bull; Working with the Data Analysis team to generate reports and analysis &lt;br&gt;&amp;bull; Ensuring data consistency and integrity within the data warehouse&lt;br&gt;&amp;bull; Exploring new opportunities to leverage new and existing data sets&lt;br&gt;&amp;bull; Providing support to our existing processes and tools&lt;br&gt;&amp;bull; Researching and assesses the viability of new processing and data storage technologies&lt;/p&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/97109/data-engineer-earnest-research', 'Sun, 04 Oct 2015 17:02:02 Z', '2015-10-04T17:02:02Z', 1847);
