INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1852, 'MySQL/PHP Software Developer at DC Energy (Vienna, VA)', '&lt;p&gt;You will help acquire and optimize data storage from our many sources across the web. This involves designing and implementing solutions to challenging cURL, parsing, and data storage problems using our proprietary PHP-based applications, all the while ensuring data flows seamlessly into our MySQL and custom NoSQL databases. You may also be responsible for creating dashboards and displays we use to view this data. As your experience with tackling these data acquisition and storage problems grows, you may contribute to developing our in-house frameworks.&lt;/p&gt;', 'Vienna, VA', 'http://careers.stackoverflow.com/jobs/65546/mysql-php-software-developer-dc-energy', 'Sun, 04 Oct 2015 16:02:02 Z', '2015-10-04T16:02:02Z', 1852);
