INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1854, 'Clojure Developer at Dean &amp; Company (Vienna, VA)', '&lt;p&gt;Dean and Company is looking for a Clojure Developer to develop financials systems in collaboration with affiliated trading firms and hedge funds. The system is being developed both for these affiliates and as a commercial product. You will be involved with the development from an early stage.&lt;br&gt;&lt;br&gt;The software underpinning all of this will be written mainly in Clojure, but we value developers who can think in terms of functional programming languages (e.g. Scala, Lisp, Scheme). Our ideal candidate is a polyglot with an aptitude for learning new languages.&lt;/p&gt;', 'Vienna, VA', 'http://careers.stackoverflow.com/jobs/71355/clojure-developer-dean-company', 'Sun, 04 Oct 2015 16:02:02 Z', '2015-10-04T16:02:02Z', 1854);
