INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1860, 'Devops Engineer at Earnest Research (New York, NY) (allows remote)', '&lt;p&gt;Passion for data: Data is what drives this company. As a part of the group, you will be immersed in a fast-paced environment, with an infinite amount of opportunity to learn and grow. Within the engineering team, the candidate will be exposed to the technical challenges associated with big data and cloud distributed systems.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; Manage our cloud infrastructure on AWS&lt;br&gt;&amp;bull; Implement and enforce security and privacy guidelines&lt;br&gt;&amp;bull; Design, manage, and maintain tools to automate ETL and Data Transformation processes&lt;br&gt;&amp;bull; Work closely with the Data Engineering and Data Analyst teams on enhancements to our platform&lt;br&gt;&amp;bull; Implement alerting tools and metrics to track system performance and availability &lt;br&gt;&amp;bull; Provide support to our existing processes and tools&lt;br&gt;&amp;bull; Contribute to the creation of system support documents such as run books&lt;br&gt;&amp;bull; Contribute in research and evaluation new technologies for use &lt;br&gt;&amp;bull; Help build out our team through hiring and evaluating candidates&lt;/p&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/97104/devops-engineer-earnest-research', 'Sun, 04 Oct 2015 16:02:02 Z', '2015-10-04T16:02:02Z', 1847);
