INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1869, 'Senior UI Programmer - VR at Crytek GmbH (Frankfurt, Germany)', '&lt;p&gt;Crytek is looking for a &lt;strong&gt;Senior UI Programmer &lt;/strong&gt;to support our team&amp;nbsp;&lt;strong&gt;on a virtual reality project&lt;/strong&gt;&amp;nbsp;in our Frankfurt, Germany studio.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;span&gt;Responsibilities:&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Develop, maintain, optimize and test the UI systems to work efficiently with the supported consoles and platforms.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Research and develop new systems as well as maintain and optimize existing systems inside the CryENGINE.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work to meet the project&amp;rsquo;s schedule while breaking the process into manageable tasks.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Write clear, maintainable and portable code.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Document source code.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Write technical and software design documents.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Continue to enhance programming, technological and communication skills.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work very closely with UI Artists and UX Designers.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Demonstrate ownership and responsibility.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Complete tasks in a timely manner and to a consistent high quality standard.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Demonstrate a desire to learn and grow professionally.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Frankfurt, Germany', 'http://careers.stackoverflow.com/jobs/80174/senior-ui-programmer-vr-crytek-gmbh', 'Sun, 04 Oct 2015 16:02:02 Z', '2015-10-04T16:02:02Z', 1869);
