INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1876, 'Front-End Software Developer at Viewpost (Orlando, FL)', '&lt;p&gt;Viewpost is seeking an exceptionally talented and motivated Front End Developer.&amp;nbsp;Our team is flatly structured, so you won&amp;rsquo;t find any Junior or Senior titles here. We are all developers at various stages in our career, working together to solve a very difficult problem. All opinions hold equal weight and can contribute towards the way we build our software.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Use Javascript, HTML, and CSS to build out a large-scale, SPA-like web applications&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work with&amp;nbsp;frameworks and libraries such as Angular, Ember, Backbone, or Knockout to build rich user experiences&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Opportunity to contribute to our front-end architecture as it continues to grow and evolve&lt;/li&gt;&lt;br /&gt;&lt;li&gt;This employee shall maintain or assist in the maintenance of the confidentiality, integrity, and availability of all data in physical and/or electronic format&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Orlando, FL', 'http://careers.stackoverflow.com/jobs/94686/front-end-software-developer-viewpost', 'Sun, 04 Oct 2015 16:02:02 Z', '2015-10-04T16:02:02Z', 1876);
