INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1885, '(Senior) Frontend Engineer (m/f) wanted! at Project A Ventures (Berlin, Deutschland)', '&lt;p&gt;We are looking for cutting-edge frontend experts who are willing to take responsibility in a powerful startup environment.&lt;br&gt; &lt;br&gt; You will be working across our portfolio companies in close and direct cooperation with the founding teams. Our portfolio covers a wide variety of projects, from e-commerce and marketplaces to software as a service models, from end-consumer products all the way to enterprise solutions. Please check project-a.com for further information.&lt;br&gt; In the past we have successfully ramped-up 30+ startups like &lt;a href="https://www.nu3.com/" rel="nofollow" rel="nofollow" rel="nofollow"&gt;Nu3&lt;/a&gt;, &lt;a href="http://www.contorion.de" rel="nofollow" rel="nofollow" rel="nofollow"&gt;Contorion&lt;/a&gt;, &lt;a href="http://www.minodes.com/" rel="nofollow" rel="nofollow" rel="nofollow"&gt;Minodes&lt;/a&gt;, &lt;a href="http://www.loopline-systems.com/en/" rel="nofollow" rel="nofollow" rel="nofollow"&gt;loopline&lt;/a&gt;, &lt;a href="https://www.wine-in-black.de/premium" rel="nofollow" rel="nofollow" rel="nofollow"&gt;wine-in-black&lt;/a&gt; and &lt;a href="http://spryker.com/" rel="nofollow" rel="nofollow" rel="nofollow"&gt;Spryker &lt;/a&gt;.&lt;br&gt; &lt;br&gt; We offer both, the opportunity to choose between developing our own infrastructure and working on projects you are passionate about. Project A invests in you and your skills and we foster a strong growth culture for each and every one of our employees.&amp;nbsp;&amp;nbsp;&lt;br&gt; Based in Berlin, Sao Paolo and with the first office in Poland soon to be opened in Warsaw or Wroclaw, our teams and ventures are truly international with a focus on Europe and Latin America, giving you the chance to travel if you wish.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Your tasks:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Work closely with designers and backend engineers in order to create a state-of-the-art product for our customers&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Plan, architect, integrate and develop efficient and transferable frontend features/systems&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop web applications using various JavaScript frameworks&amp;nbsp;&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Have attention to detail and identify potential performance weak spots&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Find and share frontend best-practices with team members and other developers&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Opportunity to build and lead a frontend team in the long run&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Berlin, Deutschland', 'http://careers.stackoverflow.com/jobs/54128/senior-frontend-engineer-m-f-wanted-project-a-ventures', 'Sun, 04 Oct 2015 15:02:02 Z', '2015-10-04T15:02:02Z', 1885);
