INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1890, 'iOS Senior Developer at 99Taxis (So Paulo, Brazil)', '&lt;p&gt;Precisamos da ajuda de Devs apaixonados por grandes desafios para construir o sistema mais escal&amp;aacute;vel poss&amp;iacute;vel pois nossa base de usu&amp;aacute;rios aumenta a cada minuto...mesmo!&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Temos hoje algumas vagas abertas, e dependendo de sua experi&amp;ecirc;ncia o encaixaremos em um projeto diferente. Gostamos de pessoas que topem o desafio e que queiram crescer junto com a equipe, que &amp;eacute; muito boa e competente!&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Ficou interessado?!&lt;/p&gt;', 'So Paulo, Brazil', 'http://careers.stackoverflow.com/jobs/83790/ios-senior-developer-99taxis', 'Sun, 04 Oct 2015 15:02:02 Z', '2015-10-04T15:02:02Z', 1890);
