INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1895, 'Ad Server PHP Developer at Adcash O (Sofia, Bulgaria)', '&lt;p&gt;We&amp;rsquo;re looking for a strong PHP developer to join our super-awesome team of experts and help us build the best PHP-based ad serving products in the business. You&amp;rsquo;ll be performing a key role in the development of our advertising platform and the introduction of new features for it. Your everyday tasks will be all about breaking boundaries and pushing the limits of bleeding-edge technology.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Interested? Then read on...&lt;/p&gt;', 'Sofia, Bulgaria', 'http://careers.stackoverflow.com/jobs/94676/ad-server-php-developer-adcash-o%C3%BC', 'Sun, 04 Oct 2015 14:02:02 Z', '2015-10-04T14:02:02Z', 1895);
