INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1899, 'Front End Engineer - Consultant at OnDeck (New York, NY)', '&lt;p&gt;Technology at OnDeck is a mix of building cutting edge systems to provide a world-class user experience to our customers and partners, aggregate mountains of data to make real-time lending decisions, and of course move lots of money every day. &amp;nbsp;We have an emphasis on scalability, reliability and accuracy.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What we''re building:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;"OnDeck as a Service" - direct business web and mobile applications.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Merchant loan origination and account API, used by the web applications and the sales CRM platform.&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;User tracking and feature testing infrastructure, as well as internationalizing our web offerings.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;We have exciting plans to build out mobile experience, SMS notifications, and voice-based account information system.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/97078/front-end-engineer-consultant-ondeck', 'Sun, 04 Oct 2015 14:02:02 Z', '2015-10-04T14:02:02Z', 1897);
