INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1903, 'Senior Java Engineer - Consultant at OnDeck (New York, NY)', '&lt;p&gt;Technology at OnDeck is a mix of building cutting edge systems to provide a world-class user experience to our customers and partners, aggregate mountains of data to make real-time lending decisions, and of course move lots of money every day. &amp;nbsp;We have an emphasis on scalability, reliability and accuracy.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;As a Senior Java Engineer - Consultant, you will build:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Data mining tools to facilitate statistical computing, mining and analysis&lt;/li&gt;&lt;br /&gt;&lt;li&gt;New models to assess credit and risk against historical transaction data at scale&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Web applications that offer slick and innovative user experiences for applying and interacting with OnDeck&amp;rsquo;s products&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Scalable infrastructure for real-time loans processing&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/97077/senior-java-engineer-consultant-ondeck', 'Sun, 04 Oct 2015 14:02:02 Z', '2015-10-04T14:02:02Z', 1897);
