INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1910, 'Back End Software Engineer at McLaren Applied Technologies (Woking, UK)', '&lt;p&gt;Software engineers at McLaren Applied Technologies create innovative solutions using the latest open source technologies. We need smart engineers who are passionate about technology to join our team and help us provide timely insight into data for our customers. Your technical expertise will help us design, develop, test, deploy, and enhance software solutions that move, store and analyze data quickly and efficiently. To find out more about what you could be working on please see our&amp;nbsp;&lt;a href="http://lsug-high-velocity.matsw.com/" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;London Scala User Group slides&lt;/a&gt;.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Responsibilities:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Create robust and scalable software that runs in the cloud.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop small prototypes and spikes that reduce risk.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Actively contribute to agile ceremonies.&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Woking, UK', 'http://careers.stackoverflow.com/jobs/96776/back-end-software-engineer-mclaren-applied-technologies', 'Sun, 04 Oct 2015 11:02:02 Z', '2015-10-04T11:02:02Z', 1910);
