INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1916, 'Senior Android Developer at WorldRemit (London, UK)', '&lt;p&gt;&lt;strong&gt;Our Story:&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;At WorldRemit we are changing the way people send money abroad. We&amp;rsquo;ve taken something complicated and made it simple. Tap the WorldRemit App or click our website and your international transfer is made &amp;ndash; to a bank account, cash pickup, Mobile Money, or airtime top-up. We send to more than 120 countries and the number is growing all the time.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Changing the world isn&amp;rsquo;t easy &amp;ndash; That&amp;rsquo;s why we like to hire smart people that like to make things happen. We are a small, agile team who is motivated by seeing the work they do. We are focused on clean, quality code and building a product that will be used around the world by millions and eventually billions of people.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As a Senior Android Developer you will build our very first native Android app in collaboration with the product and design team. You will help design and architect our first native mobile app and work across the entire product life cycle from concept, design, build and test to deployment to app store and support. If you have experience and a proven track record of building awesome apps, let&amp;rsquo;s get in touch.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What you&amp;rsquo;ll be doing day to day:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Write well-structured, commented, beautiful clean code&lt;/li&gt;&lt;br /&gt;&lt;li&gt;See unit testing as a benefit, not a chore&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Feed your opinions to the product team&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Be close friends with tracking tools, not enemies&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'London, UK', 'http://careers.stackoverflow.com/jobs/87646/senior-android-developer-worldremit', 'Sun, 04 Oct 2015 11:02:02 Z', '2015-10-04T11:02:02Z', 1916);
