INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1920, 'Micro-Service Developer at hybris an SAP company (Gliwice, Poland)', '&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Be a proactive technical &amp;bdquo;engine&amp;ldquo; influencing our Development Team and the whole company with innovative and creative ideas&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work within an agile development team to achieve its quality goals&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Design and architect elegant and scalable microservice solutions in clean and tested code (we like TDD!) that suits our high hybris standards and quality requirements using state of the art technologies&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Ensure fully automated testing and release processes&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Be an active member and mentor younger colleagues in one of our self-empowered Scrum Teams, producing software according to agile principles&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work within an agile, globally distributed team in a collaborative and fast-paced environment&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Contribute to the creation of the most amazing Cloud platform in the world&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Gliwice, Poland', 'http://careers.stackoverflow.com/jobs/97055/micro-service-developer-hybris-an-sap-company', 'Sun, 04 Oct 2015 11:02:02 Z', '2015-10-04T11:02:02Z', 1920);
