INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1925, 'Senior Web Developer at Liquid Labs GmbH (Hamburg, Deutschland)', '&lt;p&gt;Are you interested in building the digital products and services of the future?&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;We are searching for new team members passionate about building new innovative online products in a very agile development environment. You get the chance to work independently and collaborative in a highly talented team. You will be in a high impact role in an incredible fast-paced environment and take responsibility to make ideas become reality.&amp;nbsp;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;For the position of&amp;nbsp;&lt;/span&gt;&lt;strong&gt;Senior Web Developer&lt;/strong&gt;&lt;span&gt;&amp;nbsp;we seek entrepreneurial-minded people who help innovate as well as independently execute software initiatives.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will:&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Work closely in a small team rapidly building platforms to support new startups&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Be required to solve problems creatively&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Shape your work environment together with your peers&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Be expected to help get the team moving faster and producing higher quality&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Hamburg, Deutschland', 'http://careers.stackoverflow.com/jobs/94658/senior-web-developer-liquid-labs-gmbh', 'Sun, 04 Oct 2015 10:02:02 Z', '2015-10-04T10:02:02Z', 1925);
