INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1939, 'Php Developer at TribeHired Sdn Bhd (Bangkok, Thailand)', '&lt;p&gt;You shall be working with the web development team and our CTO, where&amp;nbsp;you will:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Be part of a team that assists with building and deploying software in a standardized way&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work closely with the management team to make sure the product vision sticks in your development&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Help build our website to become more functional and responsive&amp;nbsp;&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Ensure that both functional &amp;amp; non-functional usability requirements are met through the use of information architecture practices&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Be required to provide afterhours support for code deployments, changes and general support&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Contribute design considerations for new iterations&amp;nbsp;or architectural changes to existing product&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Be encouraged to give feedback on incorporating technologies that enhance the site perfomance&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Be required to become or be a leader :)&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Bangkok, Thailand', 'http://careers.stackoverflow.com/jobs/97049/php-developer-tribehired-sdn-bhd', 'Sun, 04 Oct 2015 06:02:02 Z', '2015-10-04T06:02:02Z', 1939);
