INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1955, 'Front End Web Developer at Communicare247 (Glasgow, UK)', '&lt;p&gt;We are looking for a Front-End Web Developer who is motivated to combine the art of design with the art of programming. Responsibilities will include translation of the UI/UX designs to actual code that will produce visual elements of the application. You will work with UI/UX designs and bridge the gap between this graphical design and our core back end platform, taking an active role on both sides and defining how the application looks as well as how it works.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Based in our Corporate HQ in central Glasgow&amp;nbsp;you will also be involved in the day to day support and maintenance of our cloud based systems and occasionally when required attend our 24 hour high security operations facility in Dunoon.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The post provides additional benefits including Company Pension Scheme; Life Insurance; and on site Staff Restaurant facilities.&lt;/p&gt;', 'Glasgow, UK', 'http://careers.stackoverflow.com/jobs/99466/front-end-web-developer-communicare247', 'Sun, 04 Oct 2015 00:02:02 Z', '2015-10-04T00:02:02Z', 1955);
