INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1966, 'Software Engineer, Performance Data Analytics at SolarCity (San Mateo, CA)', '&lt;p&gt;The primary goal of the Software Engineer of Performance Data Analytics will be to use SolarCity&amp;rsquo;s extensive data resources to develop methods and techniques to better integrate widespread solar deployments with the grid.&amp;nbsp; SolarCity believes that better predictability and optimization of distributed energy systems will provide significant value to grid operators and energy market participants.&amp;nbsp; The Data Engineer will be responsible for engineering tools for that prediction and optimization.&lt;/p&gt;', 'San Mateo, CA', 'http://careers.stackoverflow.com/jobs/94617/software-engineer-performance-data-analytics-solarcity', 'Sat, 03 Oct 2015 23:02:02 Z', '2015-10-03T23:02:02Z', 1966);
