INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1968, 'Software Engineer - JavaScript at Sprout Social Inc. (Chicago, IL)', '&lt;p&gt;Sprout&amp;rsquo;s web application and user experience revolve around JavaScript. We&amp;rsquo;re looking for an outstanding JS developer to join our team of talented engineers.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Our team uses a variety of web technologies, from Backbone to React, to build highly scalable software that is used by more than 15,000 brands around the world. Companies like Adobe, Pivotal Labs, Google, Marvel, and Evernote rely on Sprout to create stronger relationships with their customers through social media.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We&amp;rsquo;re looking for smart, creative, and driven people to build great software with us. If you&amp;rsquo;re the best at what you do and have a strong command of the JavaScript, HTML and CSS stack, we&amp;rsquo;d love to talk with you.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Design and develop software features based on visual mockups or user stories&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Take ownership of any step in the software development process, from design through maintenance&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Deliver high quality code, which is both functional and performant, while working and communicating in a team environment&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Contribute to the infrastructure that the team leverages for local development&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Extend testing capabilities by writing unit tests, creating integration tests,and assisting in basic QA testing during new feature development&lt;/li&gt;&lt;br /&gt;&lt;li&gt;May be asked to lead or mentor other team members&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Chicago, IL', 'http://careers.stackoverflow.com/jobs/94618/software-engineer-javascript-sprout-social-inc', 'Sat, 03 Oct 2015 23:02:02 Z', '2015-10-03T23:02:02Z', 1959);
