INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1969, 'Senior Software Engineer at ShopAtHome.com (Greenwood Village, CO)', '&lt;p&gt;ShopAtHome.com, one of the largest coupon, cash back and rewards site on the web, is looking for a full-time Senior Software Engineer to join our team. This person will have the opportunity to work with a terrific group of developers, designers, QA testers and product managers in a dynamic, fast-paced, and entrepreneurial environment. If you are passionate about creating best in class software, we are the place to be for a rewarding career!&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Interact with a plethora of business and IT professionals, from marketing to graphic design to DBAs&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Use leading edge technology to write code that is optimized for high performance, high volume web traffic, including cache management&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop, test, deploy, and maintain web software and data to support the high traffic, multi-server, ShopAtHome.com environment&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop new software products with optimal performance in the browser and on the server&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Troubleshoot and enhance existing software where needed&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Greenwood Village, CO', 'http://careers.stackoverflow.com/jobs/99465/senior-software-engineer-shopathomecom', 'Sat, 03 Oct 2015 23:02:02 Z', '2015-10-03T23:02:02Z', 1969);
