INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1976, 'Sr. Web/Mobile Software Developer at Q2ebanking (Austin, TX)', '&lt;p&gt;Q2ebanking is seeking an innovative software developer to help build cutting edge, next generation electronic banking software.&amp;nbsp; Founded here in Austin, TX, we are a fast growing company with the charter of enabling community banks and credit unions with a highly compelling, unified online, voice, mobile and tablet banking set of services from within a single application.&amp;nbsp;&amp;nbsp;Our applications are utilized by millions of customers every day who rely on us to do their online banking via an engaging mobile user experience.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;If you are a passionate software developer interested in building compelling desktop and mobile user experiences using the latest and greatest technologies available, we want to meet you.&amp;nbsp; We''re building next generation hybrid mobile applications with Javascript, CSS3 and HTML5 as well as native iOS and Android.&lt;/p&gt;', 'Austin, TX', 'http://careers.stackoverflow.com/jobs/84921/sr-web-mobile-software-developer-q2ebanking', 'Sat, 03 Oct 2015 22:02:02 Z', '2015-10-03T22:02:02Z', 1974);
