INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1977, 'Senior Backend/DevOps Engineer at PicMonkey (Seattle, WA)', '&lt;span&gt;Here&amp;rsquo;s us:&lt;/span&gt;&lt;span&gt; looking for an engineer to work on our backend services, facilitating both our mobile and web platforms. As a small team, every member works across the entire stack from tooling/deployment all the way to HTML/CSS. We like walks in the park and keeping work fun but insanely productive.&lt;/span&gt;&lt;p&gt;&lt;br /&gt;&lt;p&gt;&lt;br /&gt;&lt;span&gt;Here&amp;rsquo;s you: &lt;/span&gt;&lt;span&gt;hella smart in the DevOps world. You love collaborating and are able to take projects from start to completion with minimal direction. You are excited about working with a small but mighty team of engineers who are hammering away at a major delivery in the next few months, where your contributions will be essential.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;span&gt;&amp;bull; Writing and maintaining Ruby scripts to create and deploy new infrastructure to AWS.&lt;/span&gt;&lt;p&gt;&lt;br /&gt;&lt;span&gt;&amp;bull; Writing and maintaining Chef cookbooks, roles and environments.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;span&gt;&amp;bull; Building, maintaining, and deploying Java web apps (Spring framework, Hibernate)&lt;/span&gt;&lt;p&gt;&lt;br /&gt;&lt;span&gt;&amp;bull; Building, maintaining, and deploying JavaScript web apps (Express, WebSockets)&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;span&gt;&amp;bull; Building, maintaining, and deploying Python web apps (Django)&lt;/span&gt;&lt;p&gt;&lt;br /&gt;&lt;span&gt;&amp;bull; Honing your skills in advanced database analysis/optimization&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;br /&gt;&lt;p&gt;', 'Seattle, WA', 'http://careers.stackoverflow.com/jobs/92011/senior-backend-devops-engineer-picmonkey', 'Sat, 03 Oct 2015 22:02:02 Z', '2015-10-03T22:02:02Z', 1977);
