INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1979, 'Swift Developer at Steamclock Software (Vancouver, BC, Canada)', '&lt;p&gt;Steamclock is looking for a bright, enthusiastic&amp;nbsp;developer to help us build wonderful iOS apps. If you love writing beautiful, high-quality software, we&amp;rsquo;d like to meet you.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;You will have the opportunity to work on:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Native iOS development, generally in Swift&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Talking to clients and users&lt;/li&gt;&lt;br /&gt;&lt;li&gt;UI and UX&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Web and cross-platform mobile development&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Anything else it takes to make awesome iOS apps&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Vancouver, BC, Canada', 'http://careers.stackoverflow.com/jobs/97036/swift-developer-steamclock-software', 'Sat, 03 Oct 2015 22:02:02 Z', '2015-10-03T22:02:02Z', 1979);
