INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1986, 'Senior Engineer - SQL Guru - Live at the Beach! at CentralReach (Pompano Beach, FL)', '&lt;p&gt;We are looking for passionate, talented senior level engineer&amp;nbsp;to&amp;nbsp;join our growing team.&amp;nbsp; The CentralReach platform is changing the way providers care for their patients with complex conditions, offering them a suite of collaboration tools while boasting a fully integrated back office solution.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Those who work best with us are people who enjoy their work, approach it with passion, and possess the sort of mindset that looks for ways to get the impossible done rather than reasons why it can&amp;rsquo;t be done.&amp;nbsp; The right candidate will focus on scalability and security as a priority.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Communication and collaboration in a &lt;strong&gt;small team environment&lt;/strong&gt; is a must.&lt;/p&gt;', 'Pompano Beach, FL', 'http://careers.stackoverflow.com/jobs/48319/senior-engineer-sql-guru-live-at-the-beach-centralreach', 'Sat, 03 Oct 2015 21:02:02 Z', '2015-10-03T21:02:02Z', 1986);
