INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1992, 'Front End Engineer at HopSkipDrive (Los Angeles, CA)', '&lt;p&gt;&lt;strong&gt;&lt;strong&gt;Technology&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;HopSkipDrive is securely seated right in the middle of the rapidly growing Sharing Economy market. &amp;nbsp;We leverage aspects of Geographic Information Services (GIS), Internet of Things (IoT), Big Data, Machine Learning, Microservices, and Cloud Architectures, solving the current technical challenges of the day.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Reports to VP of Engineering&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Resolve defects and develop features on our services, web or mobile products&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Develop unit tests&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Develop functional tests against consumer products&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Write test plans for new and existing features&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Perform regression testing with the team&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Participate in sprint planning, daily scrum, retrospectives and brainstorming with the team&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Participate in developing a startup culture that focuses on quality, safety, and fun&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Los Angeles, CA', 'http://careers.stackoverflow.com/jobs/97029/front-end-engineer-hopskipdrive', 'Sat, 03 Oct 2015 20:02:02 Z', '2015-10-03T20:02:02Z', 1992);
