INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1994, 'Sr. Software Engineer at Forensiq (New York, NY)', '&lt;p&gt;At Forensiq, we are passionate about fighting online fraud. This passion has allowed us to make ourselves known in the industry, and has spurred major growth for our company. We are looking add a Senior Software Engineer to our growing team, that will further develop our tools, helping to unmask and prevent fraudulent behavior in online advertising.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Participate in the development of our core product, as well as supporting tactical and strategic planning of technology and process&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Analyze and make recommendations on all layers of the SaaS stack&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Add to our culture of personal accountability, peer participation, and discovery&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/94599/sr-software-engineer-forensiq', 'Sat, 03 Oct 2015 20:02:02 Z', '2015-10-03T20:02:02Z', 1994);
