INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2003, 'Software Engineer at NightOwl (New York, NY)', '&lt;p&gt;We are looking for a talented software engineer to help us grow and mature our newly released iOS app.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;After an intense development process, we have secured Seed Funding and are beginning to roll out our app to new markets. The app includes Ruby on Rails running in a Linux environment with a MySQL backend.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As we deploy to more markets, we are looking to find someone who can help us continuously improve and optimize our existing code base. The person we&amp;rsquo;re looking for will be comfortable thinking outside the box in a small, fun startup environment. They will help us analyze&amp;nbsp;analytics, build out our roadmap, and implement our product vision.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Develop new features for our platform&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Support existing platform by identifying and developing fixes and optimizations to our products&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Provide input into our product feature roadmap&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/97023/software-engineer-nightowl', 'Sat, 03 Oct 2015 20:02:02 Z', '2015-10-03T20:02:02Z', 2003);
