INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2010, 'Android Developer - RTP, North Carolina at R65 Labs (Durham, NC)', '&lt;p&gt;R65 Labs, a digital health startup located in Downtown Durham, creates innovative tools to help individuals and enterprises think, feel and perform better.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The Company is developing both mobile and web-based solutions that enable users to build awareness and derive actionable insights into their health. R65 Labs places significant emphasis on implementing leading edge technologies while creating unparalleled user experiences.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;R65 Labs is hiring flexible, organized, and detail-oriented Android Developers to join our Durham-based development team. &amp;nbsp;This is an opportunity to join an exciting, young company and to make a significant impact on personal health education and wellness.&lt;/p&gt;', 'Durham, NC', 'http://careers.stackoverflow.com/jobs/97017/android-developer-rtp-north-carolina-r65-labs', 'Sat, 03 Oct 2015 19:02:02 Z', '2015-10-03T19:02:02Z', 2010);
