INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2013, 'Full Stack Engineer (node.js) at HopSkipDrive (Los Angeles, CA)', '&lt;p&gt;&lt;strong&gt;&lt;strong&gt;Technology&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;HopSkipDrive is securely seated right in the middle of the rapidly growing Sharing Economy market. &amp;nbsp;We leverage aspects of Geographic Information Services (GIS), Internet of Things (IoT), Big Data, Machine Learning, Microservices, and Cloud Architectures, solving the current technical challenges of the day.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Reports to VP of Engineering&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Resolve defects and develop features on our services, web or mobile products&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop unit tests&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop functional tests against consumer products&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Write test plans for new and existing features&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Perform regression testing with the team&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Participate in sprint planning, daily scrum, retrospectives and brainstorming with the team&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Participate in developing a startup culture that focuses on quality, safety, and fun&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Los Angeles, CA', 'http://careers.stackoverflow.com/jobs/97016/full-stack-engineer-nodejs-hopskipdrive', 'Sat, 03 Oct 2015 19:02:02 Z', '2015-10-03T19:02:02Z', 1992);
