INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2019, 'Senior QA Engineer at HyTrust (Mountain View, CA)', '&lt;p&gt;HyTrust is seeking a Senior QA Engineer to help improve the quality and performance of its award-winning flagship product, &lt;strong&gt;HyTrust CloudControl&lt;/strong&gt;. The ideal candidate will combine hands-on experience on one or more virtualization platforms, an understanding of the ecosystem in large customer environments, and an instinct for uncovering quality, usability and performance issues.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The HyTrust product suite provides control and visibility for virtualized infrastructures as well as encryption and key management capabilities for cloud-hosted virtual machines and data.&lt;/p&gt;', 'Mountain View, CA', 'http://careers.stackoverflow.com/jobs/94476/senior-qa-engineer-hytrust', 'Sat, 03 Oct 2015 18:02:02 Z', '2015-10-03T18:02:02Z', 2019);
