INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2027, 'Eclectic software developer at Develer Srl (Campi Bisenzio, Italy)', '&lt;p&gt;We are looking for a passionate developer to join one of our teams working on products like CAD software, home automation software, medical equipment or one of our research projects.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We offer a developer-centric environment with the following benefits:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;24/7 office access: work any time of the day you are more productive&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Large desks in quiet offices, with large or multi monitors and powerful PCs&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Symmetric 100Mb internet&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Informal environment: free coffee, drinks and snacks, relax room with fussball, retrogaming, consoles, music instruments...&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;Please note that we require relocation in Florence and proficiency in Italian language.&lt;/p&gt;', 'Campi Bisenzio, Italy', 'http://careers.stackoverflow.com/jobs/96667/eclectic-software-developer-develer-srl', 'Sat, 03 Oct 2015 17:02:02 Z', '2015-10-03T17:02:02Z', 2027);
