INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2031, 'UI Developer at Abcam (Cambridgeshire, UK)', '&lt;p&gt;Abcam is looking for as software engineer, specialising in front-end application development who can deliver effectively within a multi-disciplinary Agile project team.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Duties&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Work with other members of the project team to estimate story difficulty.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Pair with other developers in a project team to deliver stories.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work closely with Business Analysts and QAs to ensure a quality delivery according to requirements&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work with other members of the project team to overcome technical difficulty&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Be responsible for delivered software refactoring where needed and appropriate&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Working with the rest of the team to deliver high quality working user interfaces according to templates and themes designed by our UX team.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Drive consistent interface and patterns across all applications on the Abcam platform&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Any other duties as directed by the Head of Software Development&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&amp;nbsp;&lt;strong&gt;Summary of Job Requirements:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;The specific strong UI skills we are looking for are Javascript, CSS3, HTML, JQuery, JQueryUI and MVVM (e.g. Angular JS, Knockout)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience of most of the following C#, MVC, ASP.NET, TDD, AGILE, SCRUM (WebAPI, Relational DBs ORMs e.g. nHibernate would be a bonus).&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience in working closely with backend technologies and integrating with these&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You will need to be proficient in techniques used in producing designs of complex IT systems.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience in unit / integration / regression testing and test driven development.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Familiarity with various web architectures, platforms, and frameworks.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Familiarity with the continuous integration and continuous deployment of software.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Bachelor&amp;rsquo;s degree in Computer Science or numerate field.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience in e-commerce.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Cambridgeshire, UK', 'http://careers.stackoverflow.com/jobs/97002/ui-developer-abcam', 'Sat, 03 Oct 2015 17:02:02 Z', '2015-10-03T17:02:02Z', 2025);
