INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2032, 'Front End Developer at Clockwork (Minneapolis, MN)', '&lt;p&gt;Clockwork is looking for an front end developer to be part of a team crafting enduring digital products and experiences&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;QUALIFICATIONS:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Understanding of web standards, best practices, responsive design, and emerging web technologies&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Proficiency in HTML, CSS and Javascript&lt;/li&gt;&lt;br /&gt;&lt;li&gt;An understanding of digital design principles&lt;/li&gt;&lt;br /&gt;&lt;li&gt;2+ years professional interactive production experience&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;YOU HAVE:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;A dazzling portfolio of professional work&lt;/li&gt;&lt;br /&gt;&lt;li&gt;A knack for user-focused critical thinking&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Proficiency in standard UI and development software and tools (Adobe Photoshop, Adobe Illustrator and code editing software)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience with common frameworks (like Bootstrap, Foundation, AngularJS, and UIKit)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience with common platforms (like Drupal and WordPress)&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Minneapolis, MN', 'http://careers.stackoverflow.com/jobs/96999/front-end-developer-clockwork', 'Sat, 03 Oct 2015 17:02:02 Z', '2015-10-03T17:02:02Z', 107);
