INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2046, '(Junior) Software Engineer at msg life AG (Leinfelden-Echterdingen, Deutschland)', '&lt;p&gt;&lt;strong&gt;Suchen Sie Abwechslung statt Alltagsroutine? Teamspirit statt starrer Hierarchien? Dann sind Sie bei uns richtig. Wachsen Sie mit uns. Werden Sie Teil unseres erfolgreichen Teams. Wir suchen zum n&amp;auml;chstm&amp;ouml;glichen Zeitpunkt mehrere neue Mitarbeiterinnen oder Mitarbeiter als&lt;br&gt;&lt;br&gt;(Junior) Software Engineer m/w&lt;br&gt;Java-Entwicklung | Standardsoftware f&amp;uuml;r Lebensversicherungen&lt;br&gt;&lt;br&gt;am Standort Hamburg, K&amp;ouml;ln, Leinfelden-Echterdingen oder M&amp;uuml;nchen | Referenznummer 0504-F&lt;br&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Ihre Aufgaben:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Weiterentwicklung unserer Standardsoftware msg.Life Factory, unseres Bestandsverwaltungssystems fur Lebensversicherungen&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Java-Entwicklung in einer modernen JEE-Architektur unter Verwendung aktueller Technologien (Spring, Hibernate, Maven und JBoss)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Software-Entwicklung innovativer, projektspezifischer Anpassungen und Erweiterungen sowie eigenst&amp;auml;ndige technische Umsetzung unserer Fachkonzepte&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Customizing der modelbasierten Oberfl&amp;auml;chen, der Plausibilit&amp;auml;ten, der Businesslogik, der Schnittstellen und der Batchprogramme&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Durchf&amp;uuml;hrung von Entwicklertests (inklusive Erstellung der Entwicklertestf&amp;auml;lle)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Mitwirkung bei der systemtechnischen Integration der msg.Life Factory in die kundenspezifische IT-Umgebung&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Teamorientierte Zusammenarbeit mit Kolleginnen und Kollegen verschiedener Bereiche; Zusammenarbeit und gegenseitige Unterstutzung zur Erreichung unserer gemeinsamen Ziele&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Leinfelden-Echterdingen, Deutschland', 'http://careers.stackoverflow.com/jobs/89195/junior-software-engineer-msg-life-ag', 'Sat, 03 Oct 2015 15:02:02 Z', '2015-10-03T15:02:02Z', 2046);
