INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2048, 'Know JavaScript? Join us in building a payment product using ReactJS! at Pangea (Chicago, IL)', '&lt;p&gt;We&amp;rsquo;re an early stage startup seeking a engineer&amp;nbsp;with a passion for building great&amp;nbsp;user experiences. We&amp;rsquo;re in an exciting and rapidly evolving industry that requires constant innovation, evaluation, and iteration. We believe innovation is a result of collaboration across teams. As such, this position will require flexibility, regular coordination and effective communication with the creative, engineering, product, and marketing teams.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The right engineer&amp;nbsp;has cutting-edge JavaScript&amp;nbsp;skills and excellent interpersonal communication. Pangea is located in the River North neighborhood at State and Kinzie.&lt;/p&gt;', 'Chicago, IL', 'http://careers.stackoverflow.com/jobs/94570/know-javascript-join-us-in-building-a-payment-pangea', 'Sat, 03 Oct 2015 15:02:02 Z', '2015-10-03T15:02:02Z', 2048);
