INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2050, 'PHP Developer (m/w) at Sedo GmbH (Kln, Deutschland)', '&lt;p&gt;Wir sind ein etabliertes Unternehmen in einer dynamischen Branche und bieten neben einer abwechslungsreichen T&amp;auml;tigkeit, aussichtsreiche Weiterentwicklungsm&amp;ouml;glichkeiten und eine gute Arbeitsatmosph&amp;auml;re.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Wir suchen zur Unterst&amp;uuml;tzung unserer Entwicklungsabteilung ab sofort eine/n&lt;br&gt;&lt;br&gt;&lt;strong&gt;PHP Developer (m/w)&lt;/strong&gt;&lt;br&gt;&lt;strong&gt;in K&amp;ouml;ln&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Eingesetzte Technologien &amp;amp; Methoden: SCRUM, Composer, Symfony Components, RabbitMQ, Git, Docker, Vagrant, Continuous Integration&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Ihre Aufgaben&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Design und Entwicklung von Requirements/ User Stories f&amp;uuml;r Backend Prozesse und Pr&amp;auml;sentationsschicht f&amp;uuml;r unsere Webseite www.sedo.com&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Weiterentwicklung, Optimierung und technische Sicherstellung der Funktionen unserer selbst entwickelten Plattform&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Optimierung und Weiterentwicklung der hoch performanten und skalierbaren Architektur&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Mitarbeit im SCRUM Team - von der von Analyse/Design &amp;uuml;ber Implementierung und Test bis zur Einf&amp;uuml;hrung und Betreuung&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Kln, Deutschland', 'http://careers.stackoverflow.com/jobs/96989/php-developer-m-w-sedo-gmbh', 'Sat, 03 Oct 2015 15:02:02 Z', '2015-10-03T15:02:02Z', 2050);
