INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2052, 'Frontend Web Engineer at Ambassador (Detroit, MI)', '&lt;p&gt;Ambassador is looking for talented frontend engineers who are interested in solving complex problems and shipping beautiful products as part of an ambitious, world-class engineering team. Ambassador engineers demand quality and efficiency and love taking on responsibility and freedom to make large impacts.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;WHAT YOU''LL DO&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Design and build fast and usable interfaces and tools on top of various APIs&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Collaborate with cross-functional teams to define, design, and ship extraordinary products&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Practice disciplined software engineering (automated testing, code reviews, and clean, pragmatic code)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Rapidly iterate on product to incorporate user feedback and insights and improve performance&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Be flexible, grow, and learn fast in a fun yet professional startup environment&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Detroit, MI', 'http://careers.stackoverflow.com/jobs/94567/frontend-web-engineer-ambassador', 'Sat, 03 Oct 2015 15:02:02 Z', '2015-10-03T15:02:02Z', 2052);
