INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (2063, 'UI Developer at Ventiv Technology (Atlanta, GA)', '&lt;p&gt;Help us create the next generation Risk Management Information System. Be a part of the team that is developing the new Reveal platform. This full time role located in our Atlanta, GA will work alongside the product management team in an Agile environment implementing new software functionality.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As a Front End Developer you will create efficient and reusable components and systems, identify and address security and performance issues and participate in design and code reviews. We are growing our development team with the best in the industry. Work with a team of experienced developer in a casual and fun environment.&amp;nbsp;&lt;/p&gt;', 'Atlanta, GA', 'http://careers.stackoverflow.com/jobs/92684/ui-developer-ventiv-technology', 'Sat, 03 Oct 2015 14:02:02 Z', '2015-10-03T14:02:02Z', 2063);
